INSERT INTO `countries` (`id`, `name`, `code`, `continent_id`, `created`, `modified`, `description`) VALUES
(1, 'Germany', 'DE', 1, '2017-05-04 00:00:00', '2017-05-04 00:00:00', 'Germany, officially the Federal Republic of Germany is a federal parliamentary republic in central-western Europe. Germany is the most populous member state of the European Union.'),
(2, 'Brazil', 'BRA', 5, '2017-05-04 00:00:00', '2017-05-04 00:00:00', 'Brazil, officially the Federative Republic of Brazil is the largest country in both South America and Latin America, besides being the largest of the Americas by continental area.'),
(3, 'USA', 'US', 6, '2017-05-04 00:00:00', '2017-05-04 00:00:00', 'The United States of America (USA), commonly known as the United States (U.S.) or America, is a constitutional federal republic composed of 50 states, a federal district, five major self-governing territories, and various possessions.'),
(4, 'Australia', 'AUS', 4, '2017-05-04 00:00:00', '2017-05-04 00:00:00', 'Australia, officially the Commonwealth of Australia is a country comprising the mainland of the Australian continent, the island of Tasmania and numerous smaller islands. It is the world''s sixth-largest country by total area.'),
(5, 'India', 'IND', 3, '2017-05-04 00:00:00', '2017-05-04 00:00:00', 'India, officially the Republic of India (Bhārat Gaṇarājya), is a country in South Asia. It is the seventh-largest country by area, the second-most populous country (with over 1.2 billion people), and the most populous democracy in the world.'),
(11, 'UK', 'GBR', 1, '2017-05-09 00:00:00', '2017-05-09 00:00:00', 'The United Kingdom of Great Britain and Northern Ireland, commonly known as the United Kingdom (UK) or Britain, is a sovereign country in western Europe.'),
(12, 'Spain', 'ESP', 1, '2017-05-09 00:00:00', '2017-05-09 00:00:00', 'Spain, officially the Kingdom of Spain is a sovereign state located on the Iberian Peninsula in southwestern Europe, with two large archipelagoes, the Balearic Islands in the Mediterranean Sea and the Canary Islands off the North African Atlantic coast, two cities, Ceuta and Melilla, in the North African mainland and several small islands in the Alboran Sea near the Moroccan coast.'),
(15, 'Singapore', 'SGP', 3, '2017-05-09 00:00:00', '2017-05-09 00:00:00', 'Singapore, officially the Republic of Singapore, sometimes referred to as the Lion City, the Garden City or the Little Red Dot, is a sovereign city-state in Southeast Asia. Singapore''s territory consists of one main island along with 62 other islets.'),
(16, 'South Africa', 'ZAF', 7, '2017-06-22 00:00:00', '2017-06-22 00:00:00', 'South Africa, officially the Republic of South Africa, is the southernmost country in Africa and the 25th-largest country in the world by land area.'),
(17, 'Austria', 'AUT', 1, NULL, NULL, 'Austria, officially the Republic of Austria is a federal republic and a landlocked country.'),
(18, 'Belgium', 'BEL', 1, NULL, NULL, 'Belgium, officially the Kingdom of Belgium, is a sovereign state in Western Europe.'),
(19, 'Czech Republic', 'CZE', 1, NULL, NULL, 'The Czech Republic, also known as Czechia is a nation state in Central Europe.'),
(20, 'Canada', 'CAN', 6, NULL, NULL, 'Canada is a country in the northern part of North America. Its ten provinces and three territories extend from the Atlantic to the Pacific and northward into the Arctic Ocean, covering 9.98 million square kilometres (3.85 million square miles), making it the world''s second-largest country by total area and the fourth-largest country by land area.'),
(21, 'Japan', 'JAP', 3, NULL, NULL, 'Japan is a sovereign island nation in East Asia and located in the Pacific Ocean.');
